mapper.insert:
select nvl((select max(groupkey) from ggroup)+1,0) from dual
insert into ggroup (GROUPKEY, GROUPNAME, USERKEY, AGEKEY, WHEREKEY, CATEGORYKEY, GROUPINFO, GROUPDFILE, GROUPIDORIGIN, GROUPCFILE, GROUPCORIGIN, GROUPPRIVATE, GROUPTYPE, GROUPDATE, GROUPSTATUS) values( #{groupKey}, #{groupName}, #{userKey}, #{ageKey}, #{whereKey}, #{categoryKey}, #{groupInfo}, #{groupDFile }, #{groupIdOrigin}, #{groupCFile}, #{groupCOrigin}, #{groupPrivate}, #{groupType}, sysdate, #{groupstatus}
)

mapper.select:
SELECT GROUPKEY, USERKEY, GROUPNICKNAME, USERGRADE, PROFILEFILE
FROM GGROUPMEMBER
WHERE GROUPKEY = #{groupkey} AND USERGRADE = #{grade}

SELECT COUNT(*)
FROM GGROUPMEMBER
WHERE GROUPKEY = #{groupkey} AND USERGRADE = #{grade}

SELECT GROUPKEY, USERKEY, GROUPNICKNAME, USERGRADE, PROFILEFILE
