mapper.delete:
DELETE FROM
GGROUPMEMBER
WHERE USERKEY = #{userkey} AND GROUPKEY = #{groupkey}

DELETE FROM
GGROUPMEMBER
WHERE USERKEY = #{userkey} AND GROUPKEY = #{groupkey}

mapper.update:
UPDATE GGROUPMEMBER
SET USERGRADE = USERGRADE + 1
WHERE USERKEY = #{userkey} AND GROUPKEY = #{groupkey}
