mapper.insert:
select nvl((select max(groupkey) from ggroup)+1,0) from dual
insert into ggroup values( #{groupKey}, #{groupName}, #{userKey}, #{ageKey}, #{whereKey}, #{categoryKey}, #{groupInfo}, #{groupDFile }, #{groupIdOrigin}, #{groupCFile}, #{groupCOrigin}, #{groupPrivate}, #{groupType}, sysdate, #{groupstatus}
)

mapper.select:
SELECT GROUPKEY, USERKEY, GROUPNICKNAME, USERGRADE, PROFILEFILE
FROM GGROUPMEMBER
WHERE GROUPKEY = #{groupkey} AND USERGRADE = #{grade}

SELECT COUNT(*)
FROM GGROUPMEMBER
WHERE GROUPKEY = #{groupkey} AND USERGRADE = #{grade}

SELECT GROUPKEY, USERKEY, GROUPNICKNAME, USERGRADE, PROFILEFILE
FROM GGROUPMEMBER
WHERE GROUPKEY = #{groupkey} AND USERGRADE = #{grade}

SELECT COUNT(*)
FROM GGROUPMEMBER
WHERE GROUPKEY = #{groupkey} AND USERGRADE = #{grade}

SELECT COUNT(*)
FROM GGROUP
WHERE GROUPKEY = #{groupkey} AND USERKEY = #{userkey}

mapper.delete:
DELETE FROM
GGROUPMEMBER
WHERE USERKEY = #{userkey} AND GROUPKEY = #{groupkey}
