mapper.update:
update ggroup set
groupidorigin=#{groupIdOrigin}, groupdfile=#{groupDFile} where
groupkey=#{groupKey}

update ggroup set
groupcorigin=#{groupCOrigin}, groupcfile=#{groupCFile} where
groupkey=#{groupKey}

mapper.select:
select
count(*) from ggroupmember where usergrade in(0,1) and groupkey
=#{groupkey}

select * from ggroupboard where groupkey =
#{groupkey}
order by boardseq

select userimagefile, groupnickname from gusers left
join GGROUPMEMBER using(userkey) left join calendarmember a
using(userkey) where a.groupkey=#{groupkey} and a.postkey=#{postkey}

select * from (select posttitle, to_char(cstartdate, 'YYYY-MM-DD
HH24:MI') startdate, location, groupname from (select * from
calendarmember left join calendar using(postkey) where userkey=#{userkey}) left
join (select * from post left join GGROUP using(groupkey))
using(postkey))
where startdate like #{date}

select * from (select posttitle, to_char(cstartdate, 'YYYY-MM-DD
HH24:MI') startdate, location, groupname from (select * from
calendarmember left join calendar using(postkey) where userkey=#{userkey}) left
join (select * from post left join GGROUP using(groupkey))
using(postkey))
where startdate like #{fulldate}
