mapper.select:
select * from
(select rownum r , list.* from (select b.*, (select count(*) from ggroupmember where ggroupmember.groupkey = b.groupkey) memberCount from (select * from ggroup) b order by memberCount desc) list) where r >= #{startrow} and r <= #{endrow}
(select rownum r, b.* from (select ggroup.*, (select count(groupkey) from post where post.groupkey = ggroup.groupkey group by groupkey) counta from ggroup order by counta desc) b) where r >= #{startrow} and r <= #{endrow}


SELECT COUNT(*)
FROM GGROUPMEMBER
WHERE GROUPKEY = #{groupkey} AND USERKEY = #{userkey} AND USERGRADE >= #{grade}

